clear;clc;y = @(x) x^3+x-1epsilon = 1e-5;a = 0;b = 1;counter = 0;fprintf("\t Phuong phap chia doi \n n \t a \t b \t c \t fa \t fb \t fc \t eps \n");function [nghiem] = chiadoi(f,a,b,epsilon,counter)  counter  = counter + 1;  if b-a<epsilon    nghiem = a;  else    c = (a+b)/2;    fa = f(a);    fb = f(b);    fc = f(c);        fprintf("%d \t%.3f \t%.3f \t%.3f \t%.3f \t%.3f \t%.3f \t%.3f\n",counter,a,b,c,fa,fb,fc,a-b);    if fc==0      nghiem = c;        elseif (fa*fc) < 0      nghiem = chiadoi(f,a,c,epsilon,counter);        elseif (fc*fb) < 0      nghiem = chiadoi(f,c,b,epsilon,counter);    endif;  endif;endfunction;x = chiadoi(y,a,b,epsilon,counter);xfprintf("\t Phuong phap day cung \n n \t a \t b \t c \t fa \t fb \t fc \t eps \n");function [nghiem] = daycung(f,a,b,epsilon,counter)  counter  = counter + 1;  fa = f(a);  fb = f(b);  if b-a<epsilon    nghiem = a;  else    c = (a*fb-b*fa)/(fb-fa);    fc = f(c);    fprintf("%d \t%.3f \t%.3f \t%.3f \t%.3f \t%.3f \t%.3f \t%.3f\n",counter,a,b,c,fa,fb,fc,a-b);    if fc==0      nghiem = c;        elseif (fa*fc) < 0      nghiem = chiadoi(f,a,c,epsilon,counter);        elseif (fc*fb) < 0      nghiem = chiadoi(f,c,b,epsilon,counter);    endif;  endif;endfunction;x = daycung(y,a,b,epsilon,counter);xfprintf("\n Phuong phap khai trien Taylor - Newton \n");function [nghiem] = newton(f,d,x,epsilon)  f  d  counter = 0;  format short;  fprintf("N \t x \t fx \t dx \n");  do    counter = counter + 1;    fx = f(x);    dx = d(x);    x = x - fx/dx;    fprintf("%d \t%.3f \t%.3f \t%.3f \n",counter,x,fx,dx);  until f(x) < epsilon;  nghiem = x;endfunction;f = @(x) x^3-1;d = @(x) 3*x^2;x = 4;x = newton(f,d,x,epsilon);xfprintf("\n Phuong phap cat tuyen - Sai phan  \n");function [nghiem] = saiphan(f,x0,x1,epsilon)  f  counter = 0;  fprintf("N \t x0 \t x1 \t S \n");  do    counter = counter + 1;    S = (f(x0)-f(x1))/(x0-x1);    fx = f(x1);    x0 = x1;    x1 = x1 - fx/S;    fprintf("%d \t%.3f \t%.3f \t%.3f \n",counter,x0,x1,S);  until f(x1) < epsilon;  nghiem = x1;endfunction;f = @(x) x^3+x-1;x0 = 0x1 = 1x = saiphan(f,x0,x1,epsilon);x