clear;clc;figure(1);hold on;% hàm cần tính đạo hàmf = @(x) sin(x);% đạo hàm chuẩn df = @(x) cos(x);fplot(df,[0 2]);% khoảng chiah = 0.1;% phương pháp sai phân thuậnforward_df = @(x) (f(x+h) - f(x))/h;fplot(forward_df,[0 2*pi],'ro');% phương pháp sai phân nghịchback_df = @(x) (f(x) - f(x-h))/h;fplot(back_df,[0 2*pi],'go');% phương pháp sai phân trung tâm center_df = @(x) (f(x+h) - f(x-h))/(2*h);fplot(center_df,[0 2*pi],'rx');legend('df(x) = cos(x)','Forward Diff','Back Diff','Center Diff');hold off;a = 0;b = 1;figure(2);hold on;h = @(x) ((x+3).^3)./((x-4).^7);fplot(h,[a b]);l_h = @(x) h(a) + (h(b) - h(a))/(b-a)*(x-a);fplot(l_h,[a b]);diffH = (h(a)+h(b))/2*(b-a);diffHlegend('h','l_h');figure(3);hold on;i = @(x) 1./(2*x.^2+5*x+2);fplot(i,[a b]);l_i = @(x) i(a) + (i(b) - i(a))/(b-a)*(x-a);fplot(l_i,[a b]);diffI = (i(a)+i(b))/2*(b-a);diffIlegend('i','l_i');hold off;pichia3 = pi/3;daoham = (f(pichia3+h)-f(pichia3))/h;daoham