clear;clc;printf('Phan tich ma tran LU\n');%input('Nhap n:',n);%A = rand(n,n);%A = reshape((1:25),n,n);% thay giá trị của ma trận A vào đâyA = [       3, 2, 2, 4, 5;      1, 2, 3, 4, 6;      1, 2, 3, 7, 5;      9, 2, 8, 4, 1;       1, 4, 3, 4, 5;       ];n = size(A,1);U = A;L = eye(n,n);for i=1:n-1;  if(U(i,i)==0)     continue;  endif;  for j=i+1:n;    L(j,i) = U(j,i)/U(i,i);    U(j,:) = U(j,:) - L(j,i).*U(i,:);  endfor;endfor;LU