clear;clc;mu = [40; 60];sigma = [100, 30; 30, 140];isigma = inv(sigma); detsigma = det(sigma);coeff = 1/(2*pi*sqrt(detsigma));for i=1:100   for j=1:100       x = [i;j];       xm = x - mu;       h(i,j) = exp(-0.5*xm'*isigma*xm);   end endh = h*coeff;save -6 input.mat hinit = load("output-sample2/mesh0");init = reshape(init,[100 100]);filename = strcat("output-sample2/mesh1");k = load(filename);k = reshape(k,[100 100]);figure(1);mesh(k);axis ([0 100 0 100 0 2])figure(2);imagesc(k);figure(3);contour(k);input("Enter to continue");for i=1:10:500  filename = strcat("output-sample2/mesh",num2str(i));  k = load(filename);  k = reshape(k,[100 100]);  figure(1);  mesh(k);  title(num2str(i));  figure(2);  imagesc(k);  figure(3);  contour(k);  pause(0.0001);
endfor
