clc;clear;% constantg = 9.81;f = 10^-4;% init value for U V H and its differentialnx = 40;ny = 40;D = 4400000;L = 4400000;hx = L/nx;hy = L/ny;x = 0:hx:L;y = 0:hy:L;H0 = 20000;H1 = 4400;H2 = 2660;x = y./L;y0 = 6.*hx;H = zeros(size(x,2),size(y,2));for i=1:size(x,2);  for j=1:size(y,2);    X = i*hx/L;    H(i,j) = H0 + H1.*tanh(9.0.*(y(j).-y0)/(2.0.*D)) + H2.*sin(2.*pi.*X)/(cosh(9.*y(j)-y0/D).*cosh(9.*y(j)-y0/D));    % h[i,j] = H0+H1*tanh(9.0*(y - y0)/(2.0*D))+H2*(sin(2.0*3.1415*x))/(cosh(9.0*y - y0/D)*cosh(9.0*y - y0/D))                           endfor;endfor;[dHx dHy] = get_differential_2D(H,hx,hy);U = zeros(size(H));V = zeros(size(H));% init time variable t = 0;ht = 200;T = ht*100;